import React from "react";
import { Routes, Route } from "react-router-dom";
import Home from "../pages/shared/Home";
import LoginPage from "../pages/auth/LoginPage";
import SignupPage from "../pages/auth/SignupPage";
import CivilianDashboard from "../pages/civilian/Dashboard";
import ProsecutorDashboard from "../pages/prosecutor/Dashboard";
import JudgeDashboard from "../pages/judge/Dashboard";
import RegistrarDashboard from "../pages/registrar/Dashboard";
import ProtectedRoute from "../auth/ProtectedRoute";
import AppLayout from "../layouts/AppLayout"; // ✅ import AppLayout

export default function AppRoutes() {
  return (
    <Routes>
      {/* Public routes */}
      <Route path="/" element={<Home />} />
      <Route path="/login" element={<LoginPage />} />
      <Route path="/signup" element={<SignupPage />} />

      {/* Protected role-based dashboards with AppLayout */}
      <Route
        path="/civilian"
        element={
          <ProtectedRoute>
            <AppLayout>
              <CivilianDashboard />
            </AppLayout>
          </ProtectedRoute>
        }
      />
      <Route
        path="/prosecutor"
        element={
          <ProtectedRoute>
            <AppLayout>
              <ProsecutorDashboard />
            </AppLayout>
          </ProtectedRoute>
        }
      />
      <Route
        path="/judge"
        element={
          <ProtectedRoute>
            <AppLayout>
              <JudgeDashboard />
            </AppLayout>
          </ProtectedRoute>
        }
      />
      <Route
        path="/registrar"
        element={
          <ProtectedRoute>
            <AppLayout>
              <RegistrarDashboard />
            </AppLayout>
          </ProtectedRoute>
        }
      />
    </Routes>
  );
}
