import React from "react";
import { Link } from "react-router-dom";

export default function ProsecutorDashboard() {
  const features = [
    {
      title: "üìÇ My Cases",
      desc: "View and manage all assigned cases.",
      path: "cases",
      color: "from-blue-500 to-blue-600",
    },
    {
      title: "üìë Case Details",
      desc: "Drill down into specific case details.",
      path: "cases/1",
      color: "from-indigo-500 to-indigo-600",
    },
    {
      title: "üîç Evidence",
      desc: "Review and submit new evidence files.",
      path: "evidence",
      color: "from-green-500 to-green-600",
    },
    {
      title: "üìÖ Hearings",
      desc: "Track upcoming and past hearings.",
      path: "hearings",
      color: "from-orange-500 to-orange-600",
    },
    {
      title: "üìù Submissions",
      desc: "Submit motions, responses, and legal documents.",
      path: "submissions",
      color: "from-purple-500 to-purple-600",
    },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-2xl shadow-lg p-8 mb-10">
        <h1 className="text-4xl font-extrabold mb-2">
          ‚öñÔ∏è Prosecutor Dashboard
        </h1>
        <p className="text-lg opacity-90">
          Manage cases, evidence, submissions, and hearings efficiently.
        </p>
      </div>

      {/* Grid of Features */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {features.map((feature, idx) => (
          <Link
            key={idx}
            to={feature.path}
            className="group relative bg-white shadow-lg rounded-2xl p-6 hover:shadow-2xl transition transform hover:-translate-y-2"
          >
            {/* Color strip on top */}
            <div
              className={`absolute top-0 left-0 right-0 h-2 rounded-t-2xl bg-gradient-to-r ${feature.color}`}
            />
            <div className="pt-4">
              <h2 className="text-xl font-semibold mb-2 text-gray-800">
                {feature.title}
              </h2>
              <p className="text-gray-500 group-hover:text-gray-700 transition">
                {feature.desc}
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
