import React from "react";
import { Link } from "react-router-dom";

export default function RegistrarDashboard() {
  const features = [
    {
      title: "ðŸ“‚ Case Assignments",
      desc: "Assign cases to judges and prosecutors.",
      path: "case-assignments",
      color: "from-blue-500 to-blue-600",
    },
    {
      title: "ðŸ“‘ Case Registry",
      desc: "Manage the registry of all active and closed cases.",
      path: "case-registry",
      color: "from-indigo-500 to-indigo-600",
    },
    {
      title: "ðŸ“„ Documents",
      desc: "Access and manage official court documents.",
      path: "documents",
      color: "from-purple-500 to-purple-600",
    },
    {
      title: "ðŸ“… Hearing Management",
      desc: "Schedule, reschedule, or cancel hearings.",
      path: "hearing-management",
      color: "from-orange-500 to-orange-600",
    },
    {
      title: "ðŸ’³ Payments",
      desc: "Track fees, invoices, and payment status.",
      path: "payments",
      color: "from-green-500 to-green-600",
    },
    {
      title: "ðŸ“Š Reports",
      desc: "Generate case, hearing, and financial reports.",
      path: "reports",
      color: "from-pink-500 to-pink-600",
    },
    {
      title: "ðŸ“† Schedule",
      desc: "View and manage court calendars.",
      path: "schedule",
      color: "from-teal-500 to-teal-600",
    },
    {
      title: "ðŸ‘¤ User Accounts",
      desc: "Manage civilian, prosecutor, judge, and registrar accounts.",
      path: "user-accounts",
      color: "from-red-500 to-red-600",
    },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-2xl shadow-lg p-8 mb-10">
        <h1 className="text-4xl font-extrabold mb-2">ðŸ“‹ Registrar Dashboard</h1>
        <p className="text-lg opacity-90">
          Oversee assignments, registry, hearings, and administrative tasks.
        </p>
      </div>

      {/* Grid of Features */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {features.map((feature, idx) => (
          <Link
            key={idx}
            to={feature.path}
            className="group relative bg-white shadow-lg rounded-2xl p-6 hover:shadow-2xl transition transform hover:-translate-y-2"
          >
            {/* Accent strip */}
            <div
              className={`absolute top-0 left-0 right-0 h-2 rounded-t-2xl bg-gradient-to-r ${feature.color}`}
            />
            <div className="pt-4">
              <h2 className="text-xl font-semibold mb-2 text-gray-800">
                {feature.title}
              </h2>
              <p className="text-gray-500 group-hover:text-gray-700 transition">
                {feature.desc}
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
