import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { Link } from "react-router-dom";

export default function RegistrarDashboard() {
  const menuItems = [
    { title: "üìÇ Case Assignments", text: "Assign cases to judges and prosecutors.", path: "assignments" },
    { title: "üìã Case Registry", text: "Manage the registry of all active and closed cases.", path: "registry" },
    { title: "üìë Documents", text: "Access and manage official court documents.", path: "documents" },
    { title: "üìÖ Hearing Management", text: "Schedule, reschedule, or cancel hearings.", path: "hearings" },
    { title: "üí≥ Payments", text: "Track fees, invoices, and payment status.", path: "payments" },
    { title: "üìä Reports", text: "Generate case, hearing, and financial reports.", path: "reports" },
    { title: "üóìÔ∏è Schedule", text: "View and manage court calendars.", path: "schedule" },
    { title: "üë§ User Accounts", text: "Manage civilian, prosecutor, judge, and registrar accounts.", path: "users" },
  ];

  return (
    <Container className="mt-4">
      <h2 className="mb-4 fw-bold">Registrar Dashboard</h2>
      <Row>
        {menuItems.map((item, idx) => (
          <Col md={4} className="mb-4" key={idx}>
            <Card className="shadow-sm border-0 h-100">
              <Card.Body>
                <Card.Title className="fw-bold">{item.title}</Card.Title>
                <Card.Text>{item.text}</Card.Text>
                <Link to={`/registrar/${item.path}`} className="btn btn-primary w-100">
                  Go
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}
